mapper.select:

select contentsno, cateno, pname, price, filename, stock, detail
from contents
where contentsno = #{conentsno}

select contentsno, cateno,pname, price, filename, stock from contents
pname like concat('%', #{word} ,'%')
price like concat('%', #{word} ,'%')
and cateno = #{cateno}
order by contentsno desc limit #{sno}, #{eno}

mapper.update:
update contents
set filename = #{fname}
where contentsno = #{contentsno}

mapper.select:
select contentsno, cateno,pname, price, filename, stock,rdate from contents
pname like concat('%', #{word} ,'%')
price like concat('%', #{word} ,'%')
cateno = #{word}
order by contentsno desc limit #{sno}, #{eno}

mapper.update:
update contents
set pname =#{pname},
price = #{price},
detail = #{detail},
stock = #{stock}
where contentsno =#{contentsno}
